mapper.select:
select lr.recordid, lr.uid as userid, lr.logintime, lr.ipaddress, u.uid, u.uname, u.upwd, u.role, u.lasttime from loginrecord lr left outer join user u on lr.uid = u.uid where 1 = 1
and u.uname LIKE CONCAT(CONCAT('%', #{uname, jdbcType=VARCHAR}), '%')
order by lr.logintime desc limit #{page.dbIndex, jdbcType=INTEGER}, #{page.size, jdbcType=INTEGER}

mapper.delete:
delete from loginrecord
where recordid in 

	#{recordid}
